import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { adminAuthData } from "./AdminDashboard.jsx"; // Import userAuth database

const AdminLogin = ({ setAdminUser }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const user = adminAuthData.find(
      (admin) => admin.username === username && admin.password === password
    );

    if (user) {
      setAdminUser(username); // Store authenticated user
      navigate("/admin-dashboard");
    } else {
      setError("Invalid username or password.");
    }
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h2>Admin Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{ margin: "10px", padding: "10px", width: "200px" }}
      />
      <br />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ margin: "10px", padding: "10px", width: "200px" }}
      />
      <br />
      {error && <p style={{ color: "red" }}>{error}</p>}
      <button onClick={handleLogin} style={{ padding: "10px 20px", marginTop: "10px" }}>
        Login
      </button>
    </div>
  );
};

export default AdminLogin;
